lib.calc:
var http = require("http"),
    config = require("./config").config;

var queue = [],
    busy = true,
    lastGame,
    statistic;

var unifyName = function(name) {
  return name.toLowerCase().replace(/\s+/g, "-");
};

var sameday = function(a, b) {
  return a - (a % (1000 * 60 * 60 * 24)) === b - (b % (1000 * 60 * 60 * 24));
};

var getLastGame = function(cb) {
  var opts = {
    host: config.couchdb.host,
    port: config.couchdb.port,
    path: "/" + config.couchdb.database + "/_design/league/_view/ranked_games?limit=1&descending=true",
    headers: {
      "Connection": "keep-alive",
      "Content-Encoding": "utf8",
      "Content-Type": "application/json",
      "accept": "application/json"
    }
  };
  http.get(opts, function(res) {
    var ret = "";
    res.setEncoding("utf8");
    res.on("data", function(chunk) {
      ret += chunk;
    });
    res.on("end", function() {
      lastGame = JSON.parse(ret).rows[0];
      cb();
    });
  });
};

var getStatistic = function(cb) {
  var opts = {
    host: config.couchdb.host,
    port: config.couchdb.port,
    path: "/" + config.couchdb.database + "/statistic",
    headers: {
      "Connection": "keep-alive",
      "Content-Encoding": "utf8",
      "Content-Type": "application/json",
      "accept": "application/json"
    }
  };

  http.get(opts, function(res) {
    var ret = "";
    res.setEncoding("utf8");
    res.on("data", function(chunk) {
      ret += chunk;
    });
    res.on("end", function() {
      statistic = res.statusCode !== 404 ? JSON.parse(ret) : {
        _id: "statistic",
        total: {
          games: 0,
          goals: 0,
          duration: 0,
          days: 0
        },
        goals: {
          home: 0,
          visitors: 0
        },
        victories: {
          home: 0,
          visitors: 0
        },
        games: []
      };
      cb();
    });
  });
};

var getSince = function(cb) {
  var opts = {
    host: config.couchdb.host,
    port: config.couchdb.port,
    path: "/" + config.couchdb.database,
    headers: {
      "Connection": "keep-alive",
      "Content-Encoding": "utf8",
      "Content-Type": "application/json",
      "accept": "application/json"
    }
  };

  http.get(opts, function(res) {
    var ret = "";
    res.setEncoding("utf8");
    res.on("data", function(chunk) {
      ret += chunk;
    });
    res.on("end", function() {
      cb(JSON.parse(ret).update_seq);
    });
  });
};

var observe = function(since) {
  var opts = {
    host: config.couchdb.host,
    port: config.couchdb.port,
    path: "/" + config.couchdb.database + "/_changes?feed=continuous&include_docs=true&since=" + since + "&filter=league/unranked_games",
    headers: {
      "Connection": "keep-alive",
      "Content-Encoding": "utf8",
      "Content-Type": "application/json",
      "accept": "application/json"
    }
  };

  http.get(opts, function(res) {
    var buffer = "";
    res.setEncoding("utf8");
    res.on("data", function(chunk) {
      buffer += chunk;
      try {
        var ret = JSON.parse(buffer);
        ret.doc && queue.push(ret.doc);
        since = ret.seq || since;
        busy || calc();
        buffer = "";
      } catch(e) {}
    });
    res.on("end", function(x) {
      observe(since);
    });
  });
};

var getPlayerDocs = function(currGame, cb) {
  var toFetch = currGame.players.home.concat(currGame.players.visitors).reduce(function(a, b) { var id = unifyName(b); a[id] || (a[id] = b); return a; }, {});

  var opts = {
    host: config.couchdb.host,
    port: config.couchdb.port,
    path: "/" + config.couchdb.database + "/_all_docs?include_docs=true",
    method: "POST",
    headers: {
      "Connection": "keep-alive",
      "Content-Encoding": "utf8",
      "Content-Type": "application/json",
      "accept": "application/json"
    }
  };

  var req = http.request(opts, function(res) {
    var ret = "";
    res.setEncoding("utf8");
    res.on("data", function(chunk) {
      ret += chunk;
    });
    res.on("end", function() {
      cb(res.statusCode !== 404 ? JSON.parse(ret).rows.reduce(function(last, curr) {
        last[curr.key] = curr.doc || { "_id": curr.key, type: "player", registered: currGame.end, name: toFetch[curr.key], goals: { scored: 0, conceded: 0 }, games: { won: 0, lost: 0}, score: 0, opponents: {}, teammates: {}, graph: [], history: []};
        return last;
      }, {}) : null);
    });
  });

  req.write(JSON.stringify({"keys": Object.keys(toFetch)}));
  req.end();
};

var calcDelta = function(ls, ws, gw) {
  return parseInt(10 * (1 + gw - (1 / (1 + Math.pow(10, (ls - ws) / 400)))), 10);
};

var calcPlayer = function(player, teammate, opponents, score, won, currGame, goals, history_game, alreadyCalced) {
  var verb = won ? "won" : "lost";
  var operations = {
    won: function(a, b) {
      return a + b;
    },
    lost: function(a, b) {
      return a - b;
    }
  };

  var winner = goals.home > goals.visitors ? "home" : "visitors",
      loser = goals.home < goals.visitors ? "home" : "visitors";

  player.score = operations[verb](player.score, score);
  if (alreadyCalced) {
    player.history[0].score = operations[verb](player.history[0].score, score);
  } else {
    player.goals.scored += goals[won ? winner : loser];
    player.goals.conceded += goals[won ? loser : winner];
    ++player.games[verb];
    if (teammate) {
      var tm = player.teammates[teammate.name] = player.teammates[teammate.name] || {won: 0, lost: 0};
      ++tm[verb];
      teammate.avatar && (tm.avatar = teammate.avatar);
    }
    opponents.forEach(function(opponent) {
      var op = player.opponents[opponent.name] = player.opponents[opponent.name] || {won: 0, lost: 0};
      ++op[verb];
      opponent.avatar && (op.avatar = opponent.avatar);
    });

    var graph = {score: player.score, time: currGame.end};
    if (player.graph.length && sameday(player.graph[player.graph.length - 1].time, graph.time)) {
      player.graph[player.graph.length - 1] = graph;
    } else {
      player.graph.push(graph);
    }

    var h = JSON.parse(JSON.stringify(history_game));
    h.score = won ? score : score * -1;
    player.history.unshift(h);
  }
}

var calc = function() {
  busy = true;
  queue.sort(function(a, b) {
    return (a.end < b.end) ? -1 : ((a.end > b.end) ? 1 : 0);
  });

  var currGame = queue.shift();

  if (!currGame) {
    busy = false;
    return;
  } else {
    getPlayerDocs(currGame, function(playerDocs) {
      var history_game = {
        goals: currGame.goals.reduce(function(a, b) { ++a[b.scorer]; return a;}, { home: 0, visitors: 0 }),
        players: {
          home: currGame.players.home,
          visitors: currGame.players.visitors
        },
        end: currGame.end
      };

      for (var id in playerDocs) {
        var doc = playerDocs[id];
        currGame.players.avatars && currGame.players.avatars[doc.name] && (doc.avatar = currGame.players.avatars[doc.name]);
        doc.history.length < 5 || (doc.history.length = 4);
      }

      var goals = currGame.goals.reduce(function(last, curr) { last[curr.scorer] += curr.value; return last; }, { home: 0, visitors: 0 }),
          winner = goals.home > goals.visitors ? "home" : "visitors",
          loser = goals.home < goals.visitors ? "home" : "visitors";

      var goalValue = (goals[winner] - goals[loser]) / 10;
          goalValue > 0 || (goalValue = 0);


      var winnerDocs = currGame.players[winner].map(function(name) { return playerDocs[unifyName(name)]; }),
          loserDocs = currGame.players[loser].map(function(name) { return playerDocs[unifyName(name)]; });


      var deltas = [];

      winnerDocs.forEach(function(winnerPlayer) {
        loserDocs.forEach(function(loserPlayer) {
          deltas.push(calcDelta(loserPlayer.score, winnerPlayer.score, goalValue));
        });
      });

      var winner1 = winnerDocs[0],
          winner2 = winnerDocs[1],
          loser1 = loserDocs[0],
          loser2 = loserDocs[1];

      calcPlayer(winner1, winner2, loserDocs, deltas.length === 1 ? deltas[0] * 2 : deltas[0] + deltas[1], true, currGame, goals, history_game);
      winner2 && calcPlayer(winner2, winner1, loserDocs, deltas[2] + deltas[3], true, currGame, goals, history_game, winner1 === winner2);

      calcPlayer(loser1, loser2, winnerDocs, deltas.length === 1 ? deltas[0] * 2 : deltas[0] + deltas[2], false, currGame, goals, history_game);
      loser2 && calcPlayer(loser2, loser1, winnerDocs, deltas[1] + deltas[3], false, currGame, goals, history_game, loser1 === loser2);

      statistic.started = statistic.started || currGame.end;
      ++statistic.total.games;
      statistic.total.goals += currGame.goals.length;
      statistic.total.duration += (currGame.end - currGame.start);

      if (!sameday(lastGame ? lastGame.end : 0, currGame.end)) {
        ++statistic.total.days
      }

      var goals = currGame.goals.reduce(function(a, b) { ++a[b.scorer]; return a; }, {home: 0, visitors: 0});
      statistic.goals.home += goals.home;
      statistic.goals.visitors += goals.visitors;
      goals.home > goals.visitors ? (++statistic.victories.home) : (++statistic.victories.visitors);

      statistic.games.length > 4 && (statistic.games.length = 4);
      statistic.games.unshift(history_game);

      var toWrite = [];
      for (var id in playerDocs) {
        toWrite.push(playerDocs[id]);
      }
      currGame.ranked = true;
      toWrite.push(currGame);
      toWrite.push(statistic);

      var opts = {
        host: config.couchdb.host,
        port: config.couchdb.port,
        path: "/" + config.couchdb.database + "/_bulk_docs",
        method: "POST",
        headers: {
          "Connection": "keep-alive",
          "Content-Encoding": "utf8",
          "Content-Type": "application/json",
          "accept": "application/json",
          "Authorization": "Basic " + Buffer(config.couchdb.user + ":" + config.couchdb.password).toString("base64")
        }
      };

      var req = http.request(opts, function(res) {
        var ret = "";
        res.setEncoding("utf8");
        res.on("data", function(chunk) {
          ret += chunk;
        });
        res.on("end", function() {
          ret = JSON.parse(ret);
          if(ret.error) {
            console.error("Calc Req error: "+ret.error);
            process.exit(-1);
          } else {
            statistic["_rev"] = ret.filter(function(doc) { if (doc.id === "statistic") { return doc; }})[0].rev;
            lastGame = currGame;
            calc();
          }
        });
      });

      req.write(JSON.stringify({"docs": toWrite}));
      req.end();
    });
  }
};

getLastGame(function() {
  getStatistic(function() {
    getSince(function(since) {
      observe(since);

      var opts = {
        host: config.couchdb.host,
        port: config.couchdb.port,
        path: "/" + config.couchdb.database + "/_design/league/_view/unranked_games",
        headers: {
          "Connection": "keep-alive",
          "Content-Encoding": "utf8",
          "Content-Type": "application/json",
          "accept": "application/json"
        }
      };

      http.get(opts, function(res) {
        var ret = "";
        res.setEncoding("utf8");
        res.on("data", function(chunk) {
          ret += chunk;
        });
        res.on("end", function() {
          queue.push.apply(queue, JSON.parse(ret).rows.map(function(row) { return row.value; }));
          calc();
        });
      });
    });
  });
});

lib.config:
var fs = require("fs");

exports.config = (function() {
  var configfiles = {
    base : __dirname+"/../config.json"
  },
  config,
  reader;

  for (var file in configfiles) {
    reader = fs.readFileSync(configfiles[file]);
    if (!reader) {
      throw new Error("Couldn't read config file " + configfiles[file]);
    }

    if (!config) {
      config = JSON.parse(reader);
    } else {
      var more = JSON.parse(reader);
      for (var key in more) {
        config[key] = more[key];
      }
    }
  }
  return config;
})();

lib.mustache:
var Mustache = function() {
  var Renderer = function() {};

  Renderer.prototype = {
    otag: '{{',
    ctag: '}}',
    pragmas: {},
    buffer: [],
    pragmas_implemented: {
      'IMPLICIT-ITERATOR': true
    },
    context: {},

    render: function(template, context, partials, in_recursion) {
      if(!in_recursion) {
        this.context = context;
      }

      if(!this.includes('', template)) {
        if(in_recursion) {
          return template;
        } else {
          this.send(template);
          return;
        }
      }

      template = this.render_pragmas(template);
      var html = this.render_section(template, context, partials);
      if(in_recursion) {
        return this.render_tags(html, context, partials, in_recursion);
      }

      this.render_tags(html, context, partials, in_recursion);
    },

    /*
      Sends parsed lines
    */
    send: function(line) {
      if(line != '') {
        this.buffer.push(line);
      }
    },

    /*
      Looks for %PRAGMAS
    */
    render_pragmas: function(template) {      if(!this.includes('%', template)) {        return template;      }      var that = this;      var regex = new RegExp(this.otag + '%([\\w-]+) ?([\\w]+=[\\w]+)?' +            this.ctag);      return template.replace(regex, function(match, pragma, options) {        if(!that.pragmas_implemented[pragma]) {          throw({message:             'This implementation of mustache doesnt understand the ' +            pragma + ' pragma'});        }        that.pragmas[pragma] = {};        if(options) {          var opts = options.split('=');          that.pragmas[pragma][opts[0]] = opts[1];        }        return '';      });    },

    /*      Tries to find a partial in the curent scope and render it    */    render_partial: function(name, context, partials) {      name = this.trim(name);      if(!partials || partials[name] === undefined) {        throw({message: 'unknown_partial ' + name + ''});      }      if(typeof(context[name]) != 'object') {        return this.render(partials[name], context, partials, true);      }      return this.render(partials[name], context[name], partials, true);    },

    /*      Renders inverted (^) and normal (#) sections    */    render_section: function(template, context, partials) {      if(!this.includes('#', template) && !this.includes('^', template)) {        return template;      }      var that = this;      var regex = new RegExp(this.otag + '(\\^|\\#)\\s*(.+)\\s*' + this.ctag +              '\n*([\\s\\S]+?)' + this.otag + '\\/\\s*\\2\\s*' + this.ctag +              '\\s*', 'mg');      return template.replace(regex, function(match, type, name, content) {        var value = that.find(name, context);          if(!value || that.is_array(value) && value.length === 0) {            return that.render(content, context, partials, true);          } else {            return '';          }            return that.map(value, function(row) {              return that.render(content, that.create_context(row),                partials, true);            }).join('');            return that.render(content, that.create_context(value),              partials, true);          } else if(typeof value === 'function') {            return value.call(context, content, function(text) {              return that.render(text, context, partials, true);            });            return that.render(content, context, partials, true);          } else {            return '';          }        }      });    },

    /*      Replace {{foo}} and friends with values from our view    */    render_tags: function(template, context, partials, in_recursion) {      var that = this;      var new_regex = function() {        return new RegExp(that.otag + '(=|!|>|\\{|%)?([^\\/#\\^]+?)\\1?' +          that.ctag + '+', 'g');      };      var regex = new_regex();      var tag_replace_callback = function(match, operator, name) {        switch(operator) {          return '';          that.set_delimiters(name);          regex = new_regex();          return '';          return that.render_partial(name, context, partials);          return that.find(name, context);          return that.escape(that.find(name, context));        }      };      var lines = template.split('\n');      for(var i = 0; i < lines.length; i++) {        lines[i] = lines[i].replace(regex, tag_replace_callback, this);        if(!in_recursion) {          this.send(lines[i]);        }      }      if(in_recursion) {        return lines.join('\n');      }    },

    set_delimiters: function(delimiters) {      var dels = delimiters.split(' ');      this.otag = this.escape_regex(dels[0]);      this.ctag = this.escape_regex(dels[1]);    },

    escape_regex: function(text) {      if(!arguments.callee.sRE) {        var specials = [          '/', '.', '*', '+', '?', '|',          '(', ')', '[', ']', '{', '}', '\\'        ];        arguments.callee.sRE = new RegExp(          '(\\' + specials.join('|\\') + ')', 'g'        );      }      return text.replace(arguments.callee.sRE, '\\$1');    },

    /*      find `name` in current `context`. That is find me a value      from the view object    */    find: function(name, context) {      name = this.trim(name);      function is_kinda_truthy(bool) {        return bool === false || bool === 0 || bool;      }      var value;      if(is_kinda_truthy(context[name])) {        value = context[name];      } else if(is_kinda_truthy(this.context[name])) {        value = this.context[name];      }      if(typeof value === 'function') {        return value.apply(context);      }      if(value !== undefined) {        return value;      }      return '';    },


    /* includes tag */    includes: function(needle, haystack) {      return haystack.indexOf(this.otag + needle) != -1;    },    /*      Does away with nasty characters    */    escape: function(s) {      s = String(s === null ? '' : s);      return s.replace(/&(?!\\w+;)|[\"'<>\\]/g, function(s) {        switch(s) {        case '&': return '&amp;';        case '\\': return '\\\\';        case '\"': return '&quot;';        case '\'': return '&#39;';        case '<': return '&lt;';        case '>': return '&gt;';        default: return s;        }      });    },

    create_context: function(_context) {
      if(this.is_object(_context)) {
        return _context;
      } else {
        var iterator = '.';
        if(this.pragmas['IMPLICIT-ITERATOR']) {
          iterator = this.pragmas['IMPLICIT-ITERATOR'].iterator;
        }
        var ctx = {};
        ctx[iterator] = _context;
        return ctx;
      }
    },

    is_object: function(a) {
      return a && typeof a == 'object';
    },

    is_array: function(a) {
      return Object.prototype.toString.call(a) === '[object Array]';
    },

    /*
      Gets rid of leading and trailing whitespace
    */
    trim: function(s) {
      return s.replace(/^\s*|\s*$/g, '');
    },

    /*
      Why, why, why? Because IE. Cry, cry cry.
    */
    map: function(array, fn) {
      if (typeof array.map == 'function') {
        return array.map(fn);
      } else {
        var r = [];
        var l = array.length;
        for(var i = 0; i < l; i++) {
          r.push(fn(array[i]));
        }
        return r;
      }
    }
  };

  return({
    name: 'mustache.js',
    version: '0.3.1-dev',

    /*
      Turns a template and view into HTML
    */
    to_html: function(template, view, partials, send_fun) {
      var renderer = new Renderer();
      if(send_fun) {
        renderer.send = send_fun;
      }
      renderer.render(template, view, partials);
      if(!send_fun) {
        return renderer.buffer.join('\n');
      }
    }
  });
}();

exports.name = Mustache.name;
exports.version = Mustache.version;

exports.to_html = function() {
  return Mustache.to_html.apply(this, arguments);
};

filters.unranked_games:
function(doc, req) {
  return doc.type === 'game' && doc.players.home.length && doc.players.visitors.length && !doc.ranked;
};

filters.ranked_games:
function(doc, req) {
  return doc.type === 'game' && doc.players.home.length && doc.players.visitors.length && doc.ranked;
};

css.league:
/****************************************
 FileStructure of league.less (v1.0)
*****************************************/
/*will be merged into one file when deploying to live system*/
/*path needs to be prefixed with /../public/extensions/css/ to work for express.compiler*/

@import "vars";
@import "basic";
@import "home";
@import "ligatable";
@import "userdetail";
@import "text";
//@import "scrollbar";
@import "keyframes.css";

css.vars:
@s2k-white:#eaeaea;
@s2k-black:#1c1c1c;
@s2k-lightgrey:#cecece;
@s2k-grey:#313131;
@s2k-darkgrey:#393939;
@s2k-green:#89c23c;
@s2k-darkgreen:darken(@s2k-green,20%);
@s2k-red:#900;
@s2k-button-hi:#333;
@s2k-line-grey:#727272;
@s2k-line-darkgrey:#292929;
@s2k-box-grey:rgba(31,31,31,.7);

// transition -----------------------------------------------------------------

.s2k-transition-all(@val:none) {
	@style:all @val ease-out;
    -webkit-transition: @style;
    -moz-transition: @style;
    -o-transition: @style;
    -ms-transition: @style;
    transition: @style;
}

.s2k-transition(@val:none) {
    -webkit-transition: @val;
    -moz-transition: @val;
    -o-transition: @val;
    -ms-transition: @val;
    transition: @val;
}

#s2k-transition-time {
	.no(){
		.s2k-transition();
	}
	.short(){
		.s2k-transition-all(.2s);
	}
	.long(){
		.s2k-transition-all(1s);
	}
	.newrank(){
		.s2k-transition(top 2s ease-in-out);
	}
	.color(){
		.s2k-transition(color .2s ease-out);
	}
	.led(){
        .s2k-transition(~'opacity 1s, left 1s ease-in-out, right 1s ease-in-out');
	}
	.page(){
		.s2k-transition(left .5s ease-out);
	}
	.lightbox(){
		.s2k-transition(all .3s ease-in-out);
	}
	.meter(){
		.s2k-transition(~"width ease-out 3s, color ease-in-out 3s");
	}
}

// animation ------------------------------------------------------------------

#s2k-animation {
	.generic(@animation){
        -webkit-animation:@animation;
        -moz-animation:@animation;
        -o-animation:@animation;
        -ms-animation:@animation;
        animation:@animation;
	}

	.header(){
        -webkit-transform: translate3d(0,0,0);
        -webkit-animation:acceleratedReveal 1s forwards;
        -moz-animation:acceleratedReveal 1s forwards;
        -o-animation:acceleratedReveal 1s forwards;
        -ms-animation:acceleratedReveal 1s forwards;
        animation:acceleratedReveal 1s forwards;
	}

}

// transform ------------------------------------------------------------------

.s2k-transform(@val) {
    -webkit-transform: @val;
    -moz-transform: @val;
    -o-transform: @val;
    -ms-transform: @val;
    transform: @val;
}

#s2k-transform {
	.generic(@val){
		.s2k-transform(@val);
	}
	.lightbox(){
	    .s2k-transform(scale(0));
	}
}

.s2k-transform-origin(@val) {
    -webkit-transform-origin: @val;
    -moz-transform-origin: @val;
    -o-transform-origin: @val;
    -ms-transform-origin: @val;
    transform-origin: @val;
}

#s2k-transform-origin {
	.generic(@val){
		.s2k-transform-origin(@val);
	}
}

.s2k-transform-style(@val) {
    -webkit-transform-style: @val;
    -moz-transform-style: @val;
    -o-transform-style: @val;
    -ms-transform-style: @val;
    transform-style: @val;
}

#s2k-transform-style {
	.preserve3d(){
		.s2k-transform-style(~"preserve-3d");
	}
}

// perspective ----------------------------------------------------------------

.s2k-perspective(@val) {
    -webkit-perspective: @val;
    -moz-perspective: @val;
    -o-perspective: @val;
    -ms-perspective: @val;
    perspective: @val;
}

#s2k-perspective {
	.default(){
		.s2k-perspective(400);
	}
}

// shadow ---------------------------------------------------------------------

.s2k-shadow(@val:none) {
    -webkit-box-shadow: @val;
    -moz-box-shadow: @val;
    -ms-box-shadow: @val;
    -o-box-shadow: @val;
    box-shadow: @val;
}

#s2k-shadow {
	.none(){
		.s2k-shadow();
	}
	.led(){
    	.s2k-shadow(0 0 10px #fff);
	}
	.dialog(){
    	.s2k-shadow(0 0 10px #000);
	}
	.tab(){
    	.s2k-shadow(0 0 2px #000 inset);
	}
}

// reflection -----------------------------------------------------------------

.s2k-reflection(@val) {
    -webkit-box-reflect: @val;
    -moz-box-reflect: @val;
    -ms-box-reflect: @val;
    -o-box-reflect: @val;
    box-reflect: @val;
}

#s2k-reflection{
	.led(){
	    .s2k-reflection(below 2px);
	}
}

// border-radius --------------------------------------------------------------

.s2k-border-radius(@val:0) {
    -webkit-border-radius:@val;
    -moz-border-radius:@val;
    -o-border-radius:@val;
    -ms-border-radius:@val;
    border-radius:@val;
}

#s2k-border-radius {
	.none(){
		.s2k-border-radius();
	}
	.big(){
		.s2k-border-radius(5px);
	}
	.button(){
		.s2k-border-radius(5px);
	}
	.led(){
		.s2k-border-radius(5px);
	}
}

// box ------------------------------------------------------------------------

.s2k-box(@val) {
    -webkit-box-sizing:@val;
    -moz-box-sizing:@val;
    -o-box-sizing:@val;
    -ms-box-sizing:@val;
    box-sizing:@val;
}

#s2k-box{
	.border(){
    	.s2k-box(border-box);
	}
}

// flexbox --------------------------------------------------------------------


.s2k-flexbox-pack(@val) {
    -webkit-box-pack:@val;
    -moz-box-pack:@val;
    -o-box-pack:@val;
    -ms-box-pack:@val;
    box-pack:@val;
}
.s2k-flexbox-align(@val) {
    -webkit-box-align:@val;
    -moz-box-align:@val;
    -o-box-align:@val;
    -ms-box-align:@val;
    box-align:@val;
}
.s2k-flexbox-orient(@val) {
    -webkit-box-orient: @val;
    -moz-box-orient: @val;
    -o-box-orient: @val;
    -ms-box-orient: @val;
    box-orient: @val;
}
.s2k-flexbox-flex(@val:0) {
    -webkit-box-flex: @val;
    -moz-box-flex: @val;
    -o-box-flex: @val;
    -ms-box-flex: @val;
    box-flex: @val;
}

#s2k-flexbox{
	.display(){
		display: -webkit-box;
		display: -moz-box;
		display: -o-box;
		display: -ms-box;
		display: box;
	}

	.pack {
		.start(){
		    .s2k-flexbox-pack(start);
		}
		.justify(){
		    .s2k-flexbox-pack(justify);
		}
		.center(){
		    .s2k-flexbox-pack(center);
		}
		.end(){
		    .s2k-flexbox-pack(end);
		}
	}
	.align {
		.end(){
			.s2k-flexbox-align(end);
		}
	}

	.orient{
		.vertical(){
			.s2k-flexbox-orient(vertical);
		}
	}

	.box-flex-none(){
		.s2k-flexbox-flex();
	}
	.box-flex-one(){
		.s2k-flexbox-flex(1);
	}
	.box-flex-two(){
		.s2k-flexbox-flex(2);
	}
}

// gradient -------------------------------------------------------------------

.s2k-gradient(@val1,@val2) {
    background:(@val1 + @val2 ) / 2;
    background:-webkit-gradient(linear, left top, left bottom, from(@val1), to(@val2));
    background:-moz-linear-gradient(top, @val1, @val2);
    background:-ms-linear-gradient(top, @val1, @val2);
    background:-o-linear-gradient(top, @val1, @val2);
    background:linear-gradient(top, @val1, @val2);
}
.s2k-gradient-diagonal(@val1,@val2) {
    background:(@val1 - @val2 ) / 2;
    background:-webkit-gradient(linear, 0 0, 100% 100%, from(@val1), color-stop(.35,@val2), color-stop(.65,@val2), to(@val1));
    background:-moz-linear-gradient(left top, @val1, @val2 35%, @val2 75%, @val1);
    background:-ms-linear-gradient(left top, @val1, @val2 35%, @val2 75%, @val1);
    background:-o-linear-gradient(left top, @val1, @val2 35%, @val2 75%, @val1);
    background:linear-gradient(left top, @val1, @val2 35%, @val2 75%, @val1);
}
.s2k-gradient-radial(@val1,@val2) {
    background:(@val1 - @val2 ) / 2;
    background: -webkit-gradient(radial, 50% 50%, 0, 50% 50%, 635, from(@val1), to(@val2));
    background: -webkit-radial-gradient(@val1, @val2);
    background: -moz-radial-gradient(50% 50% 0deg,circle cover, @val1, @val1, @val2 100%);
    background: -o-gradient(radial, 50% 50%, 0, 71% 19%, 203, from(@val1), color-stop(0.9, @val1), to(@val2));
    background: -ms-gradient(radial, 50% 50%, 0, 71% 19%, 203, from(@val1), color-stop(0.9, @val1), to(@val2));
    background: gradient(radial, 50% 50%, 0, 71% 19%, 203, from(@val1), color-stop(0.9, @val1), to(@val2));
}

#s2k-gradient {
	.toolbar(){
	    .s2k-gradient(#333,#000);
	}
	.button(){
	    .s2k-gradient(@s2k-green,darken(@s2k-green, 20%));
	}
	.buttonhi(){
	    .s2k-gradient(@s2k-button-hi,darken(@s2k-button-hi, 20%));
	}
	.button2(){
	    .s2k-gradient(@s2k-lightgrey,darken(@s2k-lightgrey, 20%));
	}
	.button3(){
	    .s2k-gradient(darken(@s2k-lightgrey, 20%),darken(@s2k-lightgrey, 40%));
	}
	.cancel(){
	    .s2k-gradient(darken(@s2k-red, 20%),darken(@s2k-red, 40%));
	}
	.tab(){
	    .s2k-gradient(@s2k-darkgreen,@s2k-green);
	}
	.tabhi(){
	    .s2k-gradient(@s2k-green,@s2k-darkgreen);
	}
	.dialog(){
	    .s2k-gradient(rgba(51,51,51,.95),rgba(17,17,17,.95));
	}
	.box(){
		.s2k-gradient-diagonal(#555,#111);
	}
	.feed(){
		.s2k-gradient-radial(#252525,#121212);
	}
	.logo(){
		@gradient-start:#333333;
		@gradient-stop-1:#3C3C3C;
		@gradient-stop-2:#545454;
		@gradient-stop-3:#7C7C7C;
		@gradient-stop-4:#9C9C9C;
		@gradient-end:#3D3D3D;
		background:(@gradient-start - @gradient-end ) / 2;
		background:-webkit-gradient(linear, right bottom, left top, from(#333333), color-stop(0.08, #3C3C3C), color-stop(0.22, #545454), color-stop(0.39, #7C7C7C), color-stop(0.52, #9C9C9C), to(#3D3D3D));
		background:-moz-linear-gradient(135deg, @gradient-start, @gradient-stop-1 8%, @gradient-stop-2 22%, @gradient-stop-3 39%, @gradient-stop-4 52%, @gradient-end);
		background:-ms-linear-gradient(135deg, @gradient-start, @gradient-stop-1 8%, @gradient-stop-2 22%, @gradient-stop-3 39%, @gradient-stop-4 52%, @gradient-end);
		background:-o-linear-gradient(135deg, @gradient-start, @gradient-stop-1 8%, @gradient-stop-2 22%, @gradient-stop-3 39%, @gradient-stop-4 52%, @gradient-end);
		background:linear-gradient(135deg, @gradient-start, @gradient-stop-1 8%, @gradient-stop-2 22%, @gradient-stop-3 39%, @gradient-stop-4 52%, @gradient-end);
	}
}

// font -----------------------------------------------------------------------

.s2k-font(@val) {
	font-family:@val;
}

#s2k-font {
	.Helvetica(){
	    .s2k-font(~'Helvetica,Arial,Verdana,sans-serif');
	}
	.HelveticaNeue(){
	    .s2k-font(~'"HelveticaNeue","Helvetica Neue",Helvetica,Arial,sans-serif');
	}
}

