css.home:
/*
.homepage{
	header{
		-webkit-animation-name:acceleratedReveal;
		-webkit-animation-duration:1s;
		-moz-animation-name:acceleratedReveal;
		-moz-animation-duration:1s;
	}
}
*/

.home{
	aside{
	    position: relative;
	    padding: 30px 0 0;
        #s2k-transform-style > .preserve3d();
        #s2k-perspective > .default();
        &.showboard{
            #game{
                #s2k-animation > .generic(showBoard 1s ease-out 0s 1 alternate forwards);
            }
            #feed{
                #s2k-transform > .generic(~"translate3d(0,317px,0)");
            }
        }
        &.hideboard{
            #game{
                #s2k-animation > .generic(hideBoard .8s ease-out 0 1 normal forwards);
            }
        }
		#game{
            position: absolute;
            top:30px;
            left:0;
            right:0;
            z-index: 1;
            opacity: 0;
            height:287px;
            background: @s2k-box-grey;
            #s2k-transform-origin > .generic(50% 0);
			time{
				position: absolute;
				top:-30px;
				left:270px;
				right:270px;
				display: inline-block;
				background: @s2k-box-grey;
				padding: 8px 0 6px;
				font-weight: bold;
				font-size: 16px;
				text-align: center;
			}
			.scoreboard{
				width: 400px;
				margin: 0 auto;
				position: relative;
			}
			ol{
			    @margin:10px;
				position: absolute;
				top:@margin;
				right:@margin;
				bottom:@margin;
				left:@margin;
				li{
					position: absolute;
					a{
						display: table-cell;
						vertical-align: middle;
						height: 48px;
						padding: 0;
						font-size: 16px;
						font-weight: bold;
						background: transparent url("../../images/logos/lg_avatar.png") no-repeat top left;
						background-size: 48px;
					}
					&:nth-of-type(2n+1){
						top: 0;
					}
					&:nth-of-type(2n+2){
						bottom: 0;
					}
					&:nth-of-type(1),
					&:nth-of-type(2){
						left: 0;
						a{
							padding-left:58px;
							background-position:left top;
						}
					}
					&:nth-of-type(3),
					&:nth-of-type(4){
						right: 0;
						a{
							padding-right:58px;
							background-position:right top;
						}
					}
					&.empty{
					    display: none;
					}
				}
			}
		}
		#feed{
		    #s2k-transition-time > .long;
		    ol{
				margin: 0 0 40px;
				background: @s2k-box-grey;
				li{
					padding: 20px 0;
					border-bottom: 1px solid @s2k-line-darkgrey;
					line-height: 18px;
					width: 100%;
                    background: url("../../images/icons/ic_feedevents.svg") no-repeat 0 0;
					div{
					    display: table-cell;
					    width: 140px;
						padding: 10px 0 0 85px;
						font-weight: bold;
						vertical-align: middle;
					}
					p{
					    display: table-cell;
						margin: 0;
						line-height: inherit;
					    width: 400px;
						&:first-line{
							font-weight: bold;
						}
					}
					&.start{
					    background-position: 25px -55px;
					}
					&.goal{
					    background-position: 24px -150px;
					}
					&.foul{
					    background-position: 24px -225px;
					}
					&.abort{
					    background-position: 29px -330px;
					}
					&.end{
					    background-position: 24px -237px;
					}
					&:last-child{
						border: none;
					}
					&.show{
						-webkit-transform: translate3d(0,0,0);
						-webkit-animation-name:acceleratedReveal;
						-webkit-animation-duration:1s;
						-moz-animation-name:acceleratedReveal;
						-moz-animation-duration:1s;
					}
				}
			}
		}
	}
	bside{
		ol{
            @paddingTB:10px;
            @paddingLR:5px;
            padding: @paddingTB @paddingLR;
            background: @s2k-box-grey;
            counter-reset: rank;
            margin: 0 0 10px;
			li{
				@paddingV:5px;
				position: relative;
				padding: @paddingV 10px @paddingV 50px;
				border-bottom: 1px solid @s2k-line-darkgrey;
				font-weight: bold;
		    	&::before{
					content: "\00a0";
					position: absolute;
					top:0;
					left: 0;
					bottom: 0;
					width: 20px;
					padding: @paddingV 10px 0;
					border-right: 1px solid @s2k-line-darkgrey;
				}
				&[data-rank]::before{
					content: attr(data-rank) ".";
					counter-increment: rank;
					text-align: right;
				}
				&::after{
					content: attr(data-score);
					position: absolute;
					top:0;
					right: 10px;
					bottom: 0;
					width: 30px;
					padding: @paddingV 10px 0;
					border-left: 1px solid @s2k-line-darkgrey;
					text-align: right;
				}
				&:last-child{
					border: none;
				}
			}
			&.dyn{
			    overflow: hidden;
			    position: relative;
			    -webkit-perspective: 600;
			    -webkit-transform: translate3d(0,0,0);
			    -moz-perspective: 600;
			    li{
			        position: absolute;
			        left:@paddingLR;
			        right:@paddingLR;
			        top:350px;
    			    -webkit-transform: translate3d(0,0,0);
    			    #s2k-transition-time > .newrank;
			        &.pos1{top:@paddingTB + 0;}
			        &.pos2{top:@paddingTB + 27px;}
			        &.pos3{top:@paddingTB + 54px;}
			        &.pos4{top:@paddingTB + 81px;}
			        &.pos5{top:@paddingTB + 108px;}
			        &.pos6{top:@paddingTB + 135px;}
			        &.pos7{top:@paddingTB + 162px;}
			        &.pos8{top:@paddingTB + 189px;}
			        &.pos9{top:@paddingTB + 216px;}
			        &.pos10{top:@paddingTB + 243px;}
			        &.up{
                        -webkit-animation-name:moveUp;
                        -webkit-animation-duration:2s;
                        -webkit-animation-timing-function:linear;
                        -moz-animation-name:moveUp;
                        -moz-animation-duration:2s;
                        -moz-animation-timing-function:linear;
			        }
			        &.down{
                        -webkit-animation-name:moveDown;
                        -webkit-animation-duration:2s;
                        -webkit-animation-timing-function:linear;
                        -moz-animation-name:moveDown;
                        -moz-animation-duration:2s;
                        -moz-animation-timing-function:linear;
			        }
			    }
			}
		}
		> div{
			text-align: right;
			a{
				color: @s2k-green;
				font-size: 13px;
			}
		}
	}
}

	.scorecard{
