df.app = (function() {
  var $content = $("#content"),
      $nav = $("#mainnav"),
      $activeNav;

  var navigate = function(path, content) {
    var $replace = $content;
    $content = $(content);
    $replace.replaceWith($content);

    $activeNav && $activeNav.removeClass("active");
    $activeNav = $nav.find("a[href='" + path.replace(/^(#!\/[^\/]+).*$/, "$1") + "']");
    $activeNav.addClass("active");

    window.scrollTo(0,0);

    df.ga.track(path.replace(/^#!(.*)$/, "$1"));

